import {Card, Avatar, Button} from 'antd';
import { useCallback } from 'react'; 
import {useDispatch, useSelector} from 'react-redux';
import {logoutRequestAction} from '../reducers/userReducer';

const userProfile=()=>{
    const dispatch=useDispatch();

    const{me,isLoggingOut}=useSelector((state)=>state.userReducer);

    const onLogOut=useCallback(()=>{
        dispatch(logoutRequestAction());
    });
    return(
        <Card
            actions={[
                <div key="twit">짹짹<br/>{me&&me.Posts.length}</div>,
                <div key="followings">팔로잉<br/>{me&&me.Followings.length}</div>,
                <div key="followers">팔로워<br/>{me&&me.Followers.length}</div>,

            ]}>
            <Card.Meta
                avatar={<Avatar>{me&&me.nickname[0]}</Avatar>}
                title={me&&me.nickname} />
            <Button onClick={onLogOut} loading={isLoggingOut}>로그아웃</Button>
        </Card>
    )
}

export default userProfile;
